    int x_1,y_1,x_2,y_2;//initialize vars
    private void btnBeginSGActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBeginSGActionPerformed
        // TODO add your handling code here:
        x_1=(int)Math.floor(Math.random()*40-20);//Gens Randnum from -20 to 20
        y_1=(int)Math.floor(Math.random()*40-20);//Gens Randnum from -20 to 20
        this.btnBeginSG.setEnabled(false);//Disables this button
        do{
            x_2=(int)Math.floor(Math.random()*40-20);//Gens Randnum from -20 to 20
            y_2=(int)Math.floor(Math.random()*40-20);//Gens Randnum from -20 to 20
        }while(x_1==x_2||y_1==y_2);//Forces different numbers, so non-undefined cartesian solution
        this.btnSubmitSG.setEnabled(true);//Enables submit
        this.jtaOutQSG.setText("You are allowed a calculator. Give your answer to 2 decimal places (e.g. \"y=-1.31x-2.00\").\nQUESTION: What line crosses ("+x_1+","+y_1+"), and ("+x_2+","+y_2+")");//Output
    }//GEN-LAST:event_btnBeginSGActionPerformed

