    public String PtLnSoln(int x_1, int y_1, int x_2, int y_2){
        double Slope=((double)(y_1-y_2))/((double)(x_1-x_2));
        double Shift=((double)y_1)-Slope*x_1;
        //rounded vers:
        double rSlope=Math.round(Slope*100.0)/100.0;
        double rShift=Math.round(Shift*100.0)/100.0;
        //since nobody types +-, if solves syntax:
        if(rShift>=0){
            return ("y="+rSlope+"x"+"+"+rShift);//y=mx+b (b is positive0
        }
        else{
            return ("y="+rSlope+"x"+rShift);//y=mx-b (b is neg, automatically "-" included
        }
    }
    
    private void btnSubmitSGActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSubmitSGActionPerformed
        //make soln:
        String strSolSG=PtLnSoln(x_1,y_1,x_2,y_2);
        String strSubSG=this.txtAnsSG.getText();
        if(strSubSG.equals(strSolSG)){
            //Correct,
            this.lblOutSolnSG.setText("CONGRATULATIONS! You are correct!");
        }
        else{
            //Wrong
            this.lblOutSolnSG.setText("Try Again- Practice makes Perfect!");
        }
    }//GEN-LAST:event_btnSubmitSGActionPerformed

